"use strict";const classes={slide:"slidedUp",fade:"fadedOut",hidden:"hidden",panelized:"panelized"};function countClasses(elements,name){let num=0;for(let elem of elements){if(elem.classList.contains(name)){num++}}return num}export function slidePanel(panel,button){if(!panel.classList.contains(classes.slide)){button.click()}}export const effect={slide:{count:elements=>{return countClasses(elements,classes.slide)},up:element=>{add(element,classes.slide)},down:element=>{remove(element,classes.slide)},toggle:element=>{return toggle(element,classes.slide)}},fade:{out:element=>{add(element,classes.fade)},in:element=>{remove(element,classes.fade)},toggle:element=>{return toggle(element,classes.fade)}},display:{hide:element=>{add(element,classes.hidden)},show:element=>{remove(element,classes.hidden)},toggle:element=>{return toggle(element,classes.hidden)}},panelize:{on:element=>{add(element,classes.panelized)},off:element=>{remove(element,classes.panelized)},toggle:element=>{return toggle(element,classes.panelized)}}};function add(element,className){if(!element.classList.contains(className)){element.classList.add(className)}}function remove(element,className){if(element.classList.contains(className)){element.classList.remove(className)}}function toggle(element,className){return element.classList.toggle(className)}
